import React, { Component } from "react";
import Container from "./Container";
import Row from "./Row";
import Col from "./Col";
import SearchFormTwo from "./SearchFormTwo";
import StateInfo from "./StateInfo";
import StateList from "../data/people.json";


class PeopleContainerTwo extends Component {
  state = {
    result: [],
    search: ""
  };

  // When this component mounts, search for the movie "The Matrix"
  componentDidMount() {
    this.searchState();
  }

  searchState = () => {
    const searchQuery = this.state.search.trim();
    const searchResults = StateList.filter((state) => state.name === searchQuery);
    this.setState({ 'result': searchResults });
  };

  handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value
    });
  };

  // When the form is submitted, search the OMDB API for the value of `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.searchState();
  };

  render() {
    return (
      <Container>
        <Row>
          <Col size="md-6">
          <SearchFormTwo
              searchtype="State"
              value={this.state.search}
              handleInputChange={this.handleInputChange}
              handleFormSubmit={this.handleFormSubmit}
            />
          <Col size="md-6"/>

          </Col>
        </Row>
        <Row>
          <Col size="md-12">
            <hr />
            <StateInfo search={this.state.search} />
          </Col>
        </Row>
      </Container >
    );
  }
}

export default PeopleContainerTwo;
