  // };

  render() {
    return (
      <Container>
        <Row>
          <Col size="md-1" />
          <Col size="md-5">
            <SearchForm
              searchtype="People"
              value={this.state.search}
              handleInputChange={this.handleInputChange}
              handleFormSubmit={this.handleFormSubmit}
            />
          </Col>
          <Col size="md-5">
            <SortForm
              searchtype="People"
              value={this.state.search}
              handleFormSort={this.handleFormSort}
            />
          </Col>

          <Col size="md-1" />    
        </Row>
        <Row>
          <Col size="md-12">
            <hr />
            <PeopleInfo 
            search={this.state.search} 
            sortBy={this.sortBy}
            />
          </Col>
        </Row>
      </Container >
    );
  }
}

export default PeopleContainer;
