import React from "react";
import PeopleList from "../data/people.json";

function PeopleInfo(props) {
  console.log(props)

  const results = PeopleList.filter(people => people.name.first.toLowerCase().includes(props.search.toLowerCase()));

  return (
    <div className="text-center">
      {results.length > 0 ? (<table className="table text-center">
          <thead>
            <tr>
              <th>Sort by:</th>
              <th><button
                  className="btn"
                  onClick={() => props.sortBy('firstName')}
                >
                  First Name
                </button></th>
              <th>
              <button
                  className="btn"
                  onClick={() => props.sortBy('lastName')}
                >
                  Last Name
                </button>
              </th>
              <th>
              <button
                  className="btn"
                  onClick={() => props.sortBy('email')}
                >
                  Email
                </button>
              </th>
              <th>
                <button
                  className="btn"
                  onClick={() => props.sortBy('dob')}
                >
                  DOB
                </button>
              </th>
            </tr>
          </thead>
          <tbody>
            {
              results.map(row => (
                <tr className="employee-info">
                  <td>
                    <img src={row.img} alt={row.firstName}></img>
                  </td>
                  <td>{row.firstName}</td>
                  <td>{row.lastName}</td>
                  <td>{row.email}</td>
                  <td>{row.dob}</td>
                </tr>
              ))
            }
          </tbody>
        </table>
        // <ul className="list-group">
        //   <h2>Employee List</h2>
        //   {results.map(result => (
        //     <li className="list-group-item" key={result.id.value}>
        //       <b>{result.name.first} {result.name.last} | </b> {result.location.state} | {result.email}
        //     </li>
        //   ))}
        // </ul >
      ) : (
          <h2>No Results</h2>
        )}
    </div>
  );
}

export default PeopleInfo;
