import React, { Component } from "react";
import "./style.css";

class Form extends Component {
  // Setting the component's initial state
  state = {
    firstName: "",
    lastName: "",
    password: ""
  };

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    let value = event.target.value;
    const name = event.target.name;

    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();

    // Using this.state; Make a if/else statement to do the following
    // If first or last name is empty alert the user that they are empty
    // Else if both have values alert the user to say "Hi firstName and lastName" 

    if (this.state.firstname && this.state.lastname) {
      alert(`Hi ${this.state.firstname}. 
I'll call you Professor ${this.state.lastname}!`);
      this.setState({
        firstname: "",
        lastname: ""
      });
    }
    else {
      alert(`Need more info!`);
      this.setState({
        firstname: this.state.firstname,
        lastname: this.state.lastname
      });
    }
    this.setState({
      firstName: "",
      lastName: ""
    });

    if (this.state.password.length<6 || this.state.password.length>15) {
      alert(`Hi ${this.state.firstname}. 
Password must be 6 - 15 characters!`);
      this.setState({
        password: "",
      });

  };
  };
  render() {
    // Notice how each input has a `value`, `name`, and `onChange` prop
    return (
      <div>
        <p>
          Hello {this.state.firstName} {this.state.lastName}
        </p>
        <form className="form">
          <input
            value={this.state.firstName}
            name="firstName"
            onChange={this.handleInputChange}
            type="text"
            placeholder="First Name"
          />
          <input
            value={this.state.lastName}
            name="lastName"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Last Name"
          />
          <input
            value={this.state.password}
            name="password"
            onChange={this.handleInputChange}
            type="password"
            placeholder="Password"
          />

          <button onClick={this.handleFormSubmit}>Submit</button>
        </form>
      </div>
    );
  }
  }

export default Form;
